// export default AddActivity;


import React from 'react';
import Nav from '../Nav/Nav';


export default function Form() {
    let [activity, setActivity] = React.useState(
                {
                    name: '',
                    difficulty: 3,
                    duration: '',
                    season: 'summer',
                    countries: ''
                });

    let [errors, setErrors] = React.useState({});
    let [countries, setCountries] = React.useState([]);
    let [addCountries, setAddCountries] = React.useState([]);

    React.useEffect(() =>{
        fetch('http://localhost:3001/countries/')
        .then(resp => resp.json())
        .then(data =>{
                let myMap = data.map(el => {return {id: el.id, name: el.name}})
                myMap = myMap.sort(SortArray)
                setCountries(myMap);
            });
    },[]);

    function SortArray(x, y){
        return x.name.localeCompare(y.name, 'fr', {ignorePunctuation: true});
    }

    function handleChange(e){
        e.preventDefault();
        setActivity((prev) => ({...prev, [e.target.name]: e.target.value}));
        setErrors(validation({...activity, [e.target.name]: e.target.value}));
    }

    function handleChangeName(e){
        e.preventDefault();
        setActivity((prev) => ({...prev, name: e.target.value.trimStart().toLowerCase()}));
        setErrors(validation({...activity, name: e.target.value.trimStart().toLowerCase()}));
    }

    function handleSubmit(e){
        e.preventDefault();
        if(Object.keys(errors).length === 0){
            fetch('http://localhost:3001/activities', {
                    method: 'POST',
                    headers: {'Content-Type': 'application/json;charset=utf-8'},
                    body: JSON.stringify(
                        {
                            name: activity.name,
                            difficulty: activity.difficulty,
                            duration: activity.duration,
                            season: activity.season,
                            country: addCountries
                        })
            })
            .then(res => res.json())
            .then(res =>{
                if(res.hasOwnProperty('msg')) {
                    alert(res.msg);
                }else {
                    alert(res.error);
                }
                setActivity({name: '', difficulty: '', duration: '', season: 'summer', countries: ''});
                setAddCountries([]);
            })
            .catch(e => console.log(e));
        }
    }

    function validation(){
        let errors = {};
        const regexName = /^[a-zA-\s]+(?: [a-zA-\s]+)*$/i;
        if(activity.name === '') {
            errors.name = 'Insert the name';
        }
        if(!regexName.test(activity.name)) {
            errors.name = 'The name must has only letters';
        }
        // if(activity.name.length < 3 || activity.name.length > 20) errors.name = "The name's size isn't valid";

        if(![1,2,3,4,5].includes(parseInt(activity.difficulty))) {
            errors.difficulty = "The difficult isn't valid"
        }

        let hours = Math.trunc(Number(activity.duration));
        let minutes = hours === 0 ? Number(activity.duration) * 100 : (Number(activity.duration) % hours) * 100;

        if((hours > 8) || (minutes === 0 && hours <= 0)) {
            errors.duration = "The amount of hours is not valid";
        }
        if(minutes < 0 || minutes > 59) {
            errors.duration = "The amount of minutes is not valid";
        }
        if(!['summer','spring','autumn','winter'].includes(activity.season)) {
            errors.season = "The season isn't valid";
        }
        if(addCountries.length < 0) {
            errors.countries = "Select a country";
        }
        return errors;
    }

    function addCountry(e){
        e.preventDefault();
        if (activity.countries === "") {
            setErrors({...errors, countries: 'Select a country as'});
        }else{
            if(addCountries.includes(activity.countries)){
                setErrors({...errors, countries: 'You are already selected this country'});
                
            }else{
                setAddCountries([...addCountries, activity.countries]);
                setActivity({...activity, countries: ''});
            }
        }
    }

    function deleteCountry(id){
        let newList = addCountries.filter(country => country !== id);
        setAddCountries(newList);
    }

    return (
        <React.Fragment>
        <div>
            <Nav/>
            <div>
                    <form>
                    <div>
                                <p>New activity</p>
                    </div>
                    <div>
                        <label>Name: </label>
                        <input
                            type="text"
                            title="The name's size must be a min 3, max 20 and must has only letters"
                            autocomplete="off"
                            name="name"
                            value={activity.name}
                            onChange={(e) => handleChangeName(e)}
                        />
                    </div>

                    <div>
                        <label>Duration: </label>
                        <input
                            type="number"
                            min="0"
                            max="10"
                            step="1.00"
                            title='The activity must has a duration between 1 min and 10 hours'
                            placeholder="hrs"
                            name="duration"
                            value={activity.duration}
                            onChange={(e) => handleChange(e)}
                        />
                    </div>

                    <div>
                    <div>
                        <label>Difficult: </label>
                        <select
                            name="difficult"
                            value={activity.difficulty}
                            onChange={(e) => handleChange(e)}
                        >
                            <option value="1">So easy</option>
                            <option value="2">Easy</option>
                            <option value="3">Normal</option>
                            <option value="4">Hard</option>
                            <option value="5">So hard</option>
                        </select>
                    </div>

                    <div>
                        <label>Season: </label>
                        <select
                            name="season"
                            value={activity.season}
                            onChange={(e) => handleChange(e)}
                        >
                            <option value="summer">Summer</option>
                            <option value="spring">Spring</option>
                            <option value="autumn">Autumn</option>
                            <option value="winter">Winter</option>
                        </select>
                    </div>
                </div>

                <div>
                    <label>Countries: </label>
                        <select
                            name="countries"
                            value={activity.countries}
                            onChange={(e) => handleChange(e)}
                        >
                        <option value="deafult" selected>Select country</option>
                            {
                                countries.length > 0 && countries.map(el =>
                                    {
                                        return (
                                            <option value={el.id}>{el.name}</option>
                                        )
                                    })
                            }
                        </select>
                        <button onClick={(e) => addCountry(e)}>Add country</button>
                        </div>

                        <div>
                            <p>List of countries:</p>
                            <div>
                                {
                                    addCountries.length > 0 && addCountries.map(el =>
                                        {
                                            return (
                                                <div>
                                                    <span>{el}</span>
                                                        <button type="button" onClick={() => deleteCountry(el)}>X</button>
                                                </div>
                                            )
                                        })
                                        }
                            </div>
                        </div>
                        <button 
                            onClick={(e) => handleSubmit(e)}>Create activity
                        </button>
                    </form>
                </div>
        </div>
        </React.Fragment>
    )
}
