import {GET_COUNTRIES_ID, GET_COUNTRIES_NAME, GET_ALL_COUNTRIES} from './actions'

const initialState = {
    countries: [],
}
export default function rootReducer(state = initialState ) {
    switch (action.type) {
        case GET_ALL_COUNTRIES: 
        return{
            ...state,
            countries: action.payload
        }
        case GET_COUNTRIES_NAME: 
        return{
            ...state,
            countries: action.payload
        }
        case GET_COUNTRIES_ID: 
        return{
            ...state,
            countries: action.payload
        }
    }
}
