import { useDispatch, useSelector } from "react-redux";
import { getAllCountries } from "../../Redux/actions";
import CardCountry from "../CardCountry/CardCountry";
import NavComponent from "../Nav/Nav";
import Paginado from "../Paginado/Paginado";
import style from "./Home.module.css";
import { useEffect, useState } from "react";
import ContainerCountry from "../ContainerCountry/ContainerCard";
import LoadingPage from "../loadingPage/LoadingPage";
import SearchBar from "../Searcher/Searcher";

const Home = () => {
    let dispatch = useDispatch();
    let countries = useSelector((state) => state.countries);

    useEffect(() => {
        dispatch(getAllCountries());
    }, [dispatch]);

    return (
        <div className={style.container}>
            {countries.length === 0 ? (
                <LoadingPage />
            ) : (
                <div className={style.innerContainer}>
                    <div style={{ display: "flex", flexDirection: "row", justifyContent: "space-between" }}> 
                    <NavComponent className={style.nav} />
                    <SearchBar className={style.navAndSearch}/>
                    </div>
                    <div>
                        <ContainerCountry countries={countries} />
                    </div>
                </div>
            )}
        </div>
    );
};

export default Home;
