import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountriesId } from "../../Redux/actions";
import { useParams } from "react-router-dom";

const CountryDetail = () => {

    const dispatch = useDispatch();
    const countryId = useSelector((state) => state.countriesId)

    const {id} = useParams();

    useEffect(() => {
        dispatch(getCountriesId(id))
    },[])

    return(
        <div>
            <p>{countryId.id}</p>
            <h1>{countryId.name}</h1>
            <img src={countryId.image}/>
            <p>{countryId.continent}</p>
            <p>{countryId.capital}</p>
            <p>{countryId.subregion}</p>
        </div>
    )
}
export default CountryDetail;
