import { useDispatch, useSelector } from "react-redux";

import { getAllCountries } from '../../Redux/actions';
import CardCountry from "../CardCountry/CardCountry";
import NavComponent from "../Nav/Nav";
import Paginado from "../Paginado/Paginado";
import style from "./Home.module.css"
import { useEffect, useState } from "react";

const Home = () => {

    let countries = useSelector((state) => state.countries);
    let dispatch = useDispatch();
    const [page, setPage] = useState(1);
    const [porPage, setPorPage] = useState(10);
    const max = countries.length / porPage;

    useEffect(() => {
        dispatch(getAllCountries())
    }, [dispatch])

    // return (
    //     <div className={style.container}>
    //         <Nav />
    //         <Paginado page={page} setPage={setPage} max={max} className={style.pagination} />
    //         <div className={style.cardContainer}>
    //             {countries
    //                 .slice((page - 1) * porPage, (page - 1) * porPage + porPage)
    //                 .map((country) => (
    //                     <div className={style.cardWrapper} key={country?.id}>
    //                         <CardCountry
    //                             className={style.card}
    //                             image={country?.image}
    //                             name={country?.name}
    //                             continent={country?.continent}
    //                             id={country?.id}
    //                         />
    //                     </div>
    //                 ))}
    //         </div>
    //         <Paginado page={page} setPage={setPage} max={max} className={style.pagination} />
    //     </div>
    // );


    return (
        <div className={style.container}>
            <NavComponent className={style.nav} />
            <Paginado page={page} setPage={setPage} max={max} className={style.pagination} />
            <div className={style.cardContainer}>
                {countries
                    .slice((page - 1) * porPage, (page - 1) * porPage + porPage)
                    .map((country) => (
                        <div className={style.cardWrapper} key={country?.id}>
                            <CardCountry
                                className={style.card}
                                image={country?.image}
                                name={country?.name}
                                continent={country?.continent}
                                id={country?.id}
                            />
                        </div>
                    ))}
            </div>
            <Paginado page={page} setPage={setPage} max={max} className={style.pagination} />
        </div>
    );
}

export default Home;
