import axios from 'axios';


export const getAllCountries = () => {
    return async function (dispatch) {
        return axios('http://localhost:3001/countries')
        .then(resp => resp.json())
        .then(res_json => {
            dispatch({type: GET_ALL_COUNTRIES, payload: res_json})
        });
    }
}

export const getCountriesName = (name) => {
    return async function (dispatch) {
        return axios(`http://localhost:3001/countries?${name}`)
        .then(resp => resp.json())
        .then(res_json => {
            dispatch({type: GET_COUNTRIES_NAME, payload: res_json})
        });
    }
}

export const getCountriesId = (id) => {
    return async function (dispatch) {
        return axios(`http://localhost:3001/countries/${id}`)
        .then(resp => resp.json())
        .then(res_json => {
            dispatch({type: GET_ALL_COUNTRIES, payload: res_json})
        });
    }
}
