import {GET_COUNTRIES_ID, GET_COUNTRIES_NAME, GET_ALL_COUNTRIES} from './actions_types'

const initialState = {
    countries: [],
}
const reducer = (state = initialState, action ) => {
    switch (action.type) {
        case GET_ALL_COUNTRIES: 
        console.log(state);
        return{
            ...state,
            countries: action.payload
        }
        case GET_COUNTRIES_NAME: 
        return{
            ...state,
            countries: action.payload
        }
        case GET_COUNTRIES_ID: 
        return{
            ...state,
            countries: action.payload
        }
    }
}

export default reducer;
