import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { clearDetail, getCountriesId } from "../../Redux/actions";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const CountryDetail = () => {

    const dispatch = useDispatch();
    const countryId = useSelector((state) => state.countriesId)

    const {id} = useParams();

    useEffect(() => {
        dispatch(getCountriesId(id))

        return () => dispatch(clearDetail())
    },[])

    
    let renderActivities = country.activities === undefined ? <div><p>Loading</p></div> :
                    country.activities.length === 0 ? <div><p>This country doesn't has activities</p></div> :
                    country.activities && country.activities.map(el =>{
                        return (
                            <div key={el.id}>
                                <div>
                                    <p>{el.name}</p>
                                </div>
                                <div>
                                    <div></div>
                                    <p>{el.difficulty}</p>
                                </div>
                                <div>
                                    <p>{el.duration}</p>
                                    <div></div>
                                </div>
                                <div>
                                    <div></div>
                                    <p>{el.season}</p>
                                </div>
                            </div>
                        );
                    })


    return(
        <div>
            <p>{countryId?.id}</p>
            <h1>{countryId?.name}</h1>
            <img src={countryId?.image}/>
            <p>{countryId?.continent}</p>
            <p>{countryId?.capital}</p>
            <p>{countryId?.subregion}</p>
            <p>Area: {countryId?.area}</p>
            <p>Population: {countryId?.population}</p>
            <p>{renderActivities}</p>
            <button>
                <Link to='/home'> Back </Link>
            </button>
        </div>
    )
}
export default CountryDetail;
