import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { getAllCountries } from '../../Redux/actions';
import CardCountry from "../CardCountry/CardCountry";
import Nav from "../Nav/Nav"; 

const Home = () => {

    let countries = useSelector((state) => state.countries);
    let dispatch = useDispatch();
    let itemsPages = 10;
    const [items, setItems] = useState([])
    const [currenPage, setCurrenPage] = useState(0);

    useEffect(() => {
        dispatch(getAllCountries())
    },[])

    const handleNext = () => {
        const totalElements = countries.length;

        const nextPage = currenPage + 1;

        const firstIndex = nextPage * itemsPages;

        if(firstIndex === totalElements) return;



    }

    const handlePrev = () => {

    }

    if(items.length){
        return(
            <div>
                <Nav/>
                <button onClick={handleNext}>next</button>
                <button onClick={handlePrev}>prev</button>
                {
                    items.map((country) => {
                        return(
                            <CardCountry 
                                key={country?.id}
                                image={country?.image}
                                name={country?.name}
                                continent={country?.continent}
                                id={country?.id}
                            />
                        )
                    }).splice(0,itemsPages)
                }
                <button>next</button>
                <button>prev</button>
            </div>
        )
    }else{
        return(
            <div>
                <Nav/>
                <button onClick={handleNext}>next</button>
                <button onClick={handlePrev}>prev</button>
                {
                    items.map((country) => {
                        return(
                            <CardCountry 
                                key={country?.id}
                                image={country?.image}
                                name={country?.name}
                                continent={country?.continent}
                                id={country?.id}
                            />
                        )
                    }).splice(0,itemsPages)
                }
                <button>next</button>
                <button>prev</button>
            </div>
        )    
    }
}

export default Home;
