import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { clearDetail, getCountriesId } from "../../Redux/actions";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import Nav from "../Nav/Nav";

const CountryDetail = () => {

    const dispatch = useDispatch();
    const countryId = useSelector((state) => state.countriesId)

    const {id} = useParams();

    useEffect(() => {
        dispatch(getCountriesId(id))

        return () => dispatch(clearDetail())
    },[])

    
    let renderActivities = countryId.activities === undefined ? <div><p>Loading</p></div> :
                    countryId.activities.length === 0 ? <div><p>This country doesn't has activities</p></div> :
                    countryId.activities && countryId.activities.map(el =>{
                        return (
                            <div key={el.id}>
                                <p>{el.name}</p>
                                <p>{el.difficulty}</p>
                                <p>{el.duration}</p>
                                <p>{el.season}</p>
                            </div>
                        );
                    })


    return(
        <div>
            <div>
                <Nav/>
            </div>
            <div>
                <p>{countryId?.id}</p>
                <h1>{countryId?.name}</h1>
                <img src={countryId?.image}/>
                <p>{countryId?.continent}</p>
                <p>{countryId?.capital}</p>
                <p>{countryId?.subregion}</p>
                <p>Area: {countryId?.area}</p>
                <p>Population: {countryId?.population}</p>
                <div>{renderActivities}</div>
            </div>
        </div>
    )
}
export default CountryDetail;
